int num2 = Integer.parseInt(n2.getText());
int num3 = Integer.parseInt(n3.getText());

if ((num1 < num2)&&(num1 < num3))
    res.setText(""+num1);
else if ((num2 < num1)&&(num2 < num3))
    res.setText(""+num2);
else if ((num3 < num1)&&(num3 < num2))
    res.setText(""+num3);

