 void nextMove(String player, String [] board){
for(int i=0;i<8;i++){
for(int j=0;j<8;j++)
{
if(board[i].charAt(j)=='-'){
int c = getChange(i,j,board,player);
if(c>max){
max=c;
nx=i;
ny=j;
System.out.println("The position "+nx+" "+ny+" has a score of "+max+"\n");
}

}
}
}
if(player.equals("B")){
JTextField box = (JTextField) Panel.getComponentAt(nx*40 +20, ny*40+20);
box.setForeground(Color.BLACK);
box.setText("•");

}
}  

    private void Compute(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Compute
String player = Player_Title.getText();
String board[] = new String[8];
for(int x=0;x<8;x++){
for(int y=0;y<8;y++){ 
JTextField box = (JTextField) Panel.getComponentAt(x*40 +20, y*40+20);
if((box.getForeground()==Color.BLACK)&&(box.getText().equals("•")))
board[x]+="B";
else if((box.getForeground()==Color.WHITE)&&(box.getText().equals("•")))
board[x]+="W";
}
}
        
nextMove(player,board);        
        
    }//GEN-LAST:event_Compute

