    private void CapitalsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CapitalsActionPerformed
res.setText(""); 
String code = ta.getText();
char codea[] = code.toCharArray();
for(int i=0;i<=codea.length;i++){
Character a = codea[i];
if((a.hashCode()>=65)&&(a.hashCode()<=90))
{;}
else
res.append(""+a);
}
        
        // TODO add your handling code here:
    }//GEN-LAST:event_CapitalsActionPerformed

