    private void AltActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AltActionPerformed
res.setText(""); 
int n = Integer.parseInt(no.getText());
String code = ta.getText();
char codea[] = code.toCharArray();
for(int i=0;i<=codea.length;i+=n){
Character a = codea[i];
res.append(""+a);
}
    }//GEN-LAST:event_AltActionPerformed

    private void NumbersActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_NumbersActionPerformed
res.setText(""); 
String code = ta.getText();
char codea[] = code.toCharArray();
for(int i=0;i<=codea.length;i++){
Character a = codea[i];
if((a.hashCode()>=48)&&(a.hashCode()<=59))
{;}
else
res.append(""+a);
}
        
        
    }//GEN-LAST:event_NumbersActionPerformed

    private void reverseActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_reverseActionPerformed
res.setText(""); 
String code = ta.getText();
char codea[] = code.toCharArray();
for(int i=codea.length-1;i>=0;i--){
Character a = codea[i];
res.append(""+a);
}
    }//GEN-LAST:event_reverseActionPerformed

